import React, { useState } from "react";
import Header from "./Header";
import Navigation from "./Navigation";
import SearchBar from "./SearchBar";
import IndustryContainer from "./IndustryContainer";
import "..components/";

const App = () => {
  const [searchInput, setSearchInput] = useState("");

  const handleSearchInputChange = (input) => {
    setSearchInput(input);
  };

  const industries = [
    {
      title: "Fitness Professionals",
      companies: ["Company 1", "Company 2", "Company 3"],
    },
    {
      title: "Accountants",
      companies: ["Company 1", "Company 2", "Company 3"],
    },
    {
      title: "Beauticians",
      companies: ["Company 1", "Company 2", "Company 3"],
    },
    {
      title: "Psychologists",
      companies: ["Company 1", "Company 2", "Company 3"],
    },
    {
      title: "Groundskeepers",
      companies: ["Company 1", "Company 2", "Company 3"],
    },
    {
      title: "Maids",
      companies: ["Company 1", "Company 2", "Company 3"],
    },
    {
      title: "Veterinarians",
      companies: ["Company 1", "Company 2", "Company 3"],
    },
    {
      title: "Photographers",
      companies: ["Company 1", "Company 2", "Company 3"],
    },
    {
      title: "Personal Chefs",
      companies: ["Company 1", "Company 2", "Company 3"],
    },
    {
      title: "Lawyer",
      companies: ["Company 1", "Company 2", "Company 3"],
    },
    {
      title: "Doctors",
      companies: ["Company 1", "Company 2", "Company 3"],
    },
    // Add more industries as needed
  ];

  const filteredIndustries = industries.filter((industry) =>
    industry.title.toLowerCase().includes(searchInput.toLowerCase())
  );

  return (
    <div>
      <Header />
      <Navigation />
      <SearchBar onSearchInputChange={handleSearchInputChange} />

      <p>Suggestions:</p>

      <div className="container">
        {filteredIndustries.map((industry, index) => (
          <IndustryContainer
            key={index}
            title={industry.title}
            companies={industry.companies}
          />
        ))}
      </div>

      <div>
        <p>
          Helping Hands is an organization started in 2024 with the aim of
          simplifying people's lives by providing the necessary services
          available when required.
        </p>
      </div>

      <div className="closeNav" id="closeNav">
        <button>Contact</button>
        <button>Email</button>
        <button>Twitter</button>
        <button>Instagram</button>
        <button>Facebook</button>
        <button>Feedback</button>
      </div>
    </div>
  );
};

export default App;
